import { Segment, Form, TextArea,Button} from 'semantic-ui-react';


export default ()=>{
	return (
		<Segment style = {{height:'850px'}}>
			<div>
			 <h1> QUIKK Smart Contract Auditor</h1> <Button primary style = {{width : '250px'}} >Run Audit</Button>
			 </div>
			 <h4 style ={{color:'grey'}}> light, open-source smart contract auditor for ethereum</h4>
			 <Form>
			 <TextArea style ={{height:'700px'}} placeholder="Paste your smart contract code here..."/>
			 </Form>
		 </Segment>
		)
}
