import React from 'react';
import { Segment, Form, TextArea,Button} from 'semantic-ui-react';


class landingPageSection1 extends React.Component{

	state = {
		contract:'22'
	}


	onSubmit(){

	}

	onChange(event){

		this.setState({
			contract:event.target.value
		});
	}



	render(){
	return (
		<Segment style = {{height:'850px'}}>
			<h1> QUIKK Smart Contract Auditor</h1> 


			 <p class="row" style ={{color:'grey'}}> light, open-source smart contract auditor for ethereum</p>

			 <Form onSubmit = {this.onSubmit}>
			 <TextArea value= {this.state.contract} onChange ={this.onChange} style ={{height:'700px'}} placeholder="Paste your smart contract code here..."/>
			 <Button class="row" primary floated = "right" style ={{marginTop:'10px'}}>Run Audit</Button>
			 </Form>
		 </Segment>
		)
	}
}

export default landingPageSection1;
