import React from 'react';
import { Button,Segment, Form, TextArea} from 'semantic-ui-react';


const listOfCompilers =  ["soljson-v0.3.1+commit.c492d9b.js","soljson-v0.3.6+commit.3fc68da.js","soljson-v0.4.24+commit.e67f0147.js", 
"soljson-v0.4.26+commit.4563c3fc.js","soljson-v0.5.0+commit.1d4f565a.js","soljson-v0.5.5+commit.47a71e8f.js",
"soljson-v0.5.9+commit.e560f70d.js"
];


class landingPageSection1 extends React.Component{

constructor(props) {
    super(props);
	this.state = {
		contractCode:'',
		currentCompiler: ''
	}

	this.handleChangesToContract = this.handleChangesToContract.bind(this);
}


//when smart contract code changes update the state of the parent
	handleChangesToContract(event){

		this.props.storeContractCodeToState(event.target.value);
		this.setState({
			contract:event.target.value
		});
	}



	render(){
	return (
		<Segment style = {{height:'850px'}}>
			<h1> QUIKK Smart Contract Auditor</h1> 


			 <p style ={{color:'grey'}}> light, open-source smart contract auditor for ethereum</p>

			 <Form >
			 <TextArea value= {this.state.contract} onChange={this.handleChangesToContract} style ={{height:'700px'}} placeholder="Paste your smart contract code here..."/>
			 <Button primary floated = "right" style ={{marginTop:'10px'}} onClick ={this.onSubmit} >Run Audit</Button>
			 </Form>
		 </Segment>
		)
	}
}

export default landingPageSection1;
