import React from 'react';
import { Segment, Form, TextArea,Button} from 'semantic-ui-react';


export default ()=>{
	return (
		<Segment style = {{height:'850px'}}>
			<h1> QUIKK Smart Contract Auditor</h1> 

			 <p style ={{color:'grey', display:inline-block}}> light, open-source smart contract auditor for ethereum</p>
			 <Button primary style = {{marginLeft : '250px'}} >Run Audit</Button>

			 <Form>
			 <TextArea style ={{height:'700px'}} placeholder="Paste your smart contract code here..."/>
			 </Form>
		 </Segment>
		)
}
