import React from 'react';
import { Container, Segment, Form, TextArea,Grid, Button } from 'semantic-ui-react';
import Head from 'next/head';
import Header from './Header';
import LandingPageSection1 from './Sections/landingPageSection1';
import LandingPageSection2 from './Sections/landingPageSection2';


class landingPage extends React.Component{

	state = {
		currentCompiler:'',
		contractCode:''
	}


	onSubmit (){

		var x = this.state.contractCode.split("\n");
		var source = this.state.contract;

		//printlist of available compilers
		BrowserSolc.getVersions(function(soljsonSources, soljsonReleases) {
		  //console.log(soljsonSources);
		  //console.log(soljsonReleases);
		});

		//Load a specific compiler version
		BrowserSolc.loadVersion(this.state.currentCompiler, function(compiler) {

		  let optimize = 1;
		  let result = compiler.compile(source, optimize);
		  console.log(result);
	});
	}

	selectCompiler (compiler){
		console.log("compiler is " + compiler);

		// this.setState({
		// 	currentCompiler:compiler
		// })

	}

//take contract code from component and pass it to parent
	storeContractCodeToState(contractCode){
		console.log("contract code is " + contractCode);


		this.setState({
			contractCode: contractCode
		})
	}



	render(){

		return(
			<Container style={{width:'1230px'}}>
			  <Header/>
				<Grid>
				    <Grid.Row>

					      <Grid.Column width={12}>
					      <LandingPageSection1
					      storeContractCodeToState = {this.storeContractCodeToState}
					      />
					      </Grid.Column>


					      <Grid.Column width={4}>
					      <LandingPageSection2 
					      onSubmit ={this.onSubmit}
					      selectCompiler = {this.selectCompiler}
					      />
					      </Grid.Column>

				    </Grid.Row>
			    </Grid>
		  </Container>
		  )

	}
}

export default landingPage;
