import React from 'react';
import {Segment, Form, TextArea, Progress,Message,Table,Grid} from 'semantic-ui-react';


class landingPageSection1 extends React.Component{

constructor(props) {
    super(props);
	this.state = {
		contractCode:'',
		lineNumbers:''
	}
}


//when smart contract code changes update the state of the parent
	handleChangesToContract=(event)=>{
		this.props.removeErrorMessage(); 
		this.props.storeContractCodeToState(event.target.value);
		this.setState({
			contract:event.target.value
		});
	}

	componentDidMount(){
		this.generateNumberedList();
	}

	//generate rendered number list on side of page 
	generateNumberedList=()=>{

	let list = lineNumbers.map(numbers => {        
            return (
            	<Table.Row>
				        <Table.Cell >
				          {numbers}
				        </Table.Cell>
				 </Table.Row>
            );   
        });

	     //update loading bar
        this.setState({
		  		lineNumbers :list

		  	})

	}



	render(){
	const {Input, Group,Field} = Form;
	return (
		<Segment style = {{height:'850px',overflow: 'scroll'}}>
			<h1> QUIKK Smart Contract Auditor</h1> 
			 <p style ={{color:'grey'}}> light, open-source smart contract auditor for ethereum</p>
			 <Form error= {!!this.props.errorMessage} success={!!this.props.successMessage}>
			<Progress percent={this.props.percent} autoSuccess />
			<Message error header = "Error" content = {this.props.errorMessage}/>
			<Message success header = "Success!" content = {this.props.successMessage}/>


			<Table celled structured>

				    <Table.Body style={{fontSize:'8px',color :'grey',textAlign:'center'}}>

						      <Table.Row>

						      <Table.Cell textAlign='right' width='1'>2</Table.Cell>
						        <Table.Cell rowSpan='4'>
						        <TextArea value= {this.state.contract} onChange={this.handleChangesToContract} style ={{fontSize:'14px',minHeight:'120px'}} placeholder="Paste your smart contract code here..."/>
						        </Table.Cell>
						      </Table.Row>

						      <Table.Row>
						      <Table.Cell textAlign='right'>52</Table.Cell>
						      </Table.Row>

						      <Table.Row>
						      <Table.Cell textAlign='right'>12</Table.Cell>
						      </Table.Row>

						      <Table.Row>
						      <Table.Cell textAlign='right'>12</Table.Cell>
						      </Table.Row>

				</Table.Body>
  			</Table>

			 </Form>
		 </Segment>
		)
	}
}

export default landingPageSection1;


